import "./App.css";
import { useState, useEffect } from "react";
import Section from "./components/Section/Section";
import Notification from "./components/Notification/Notification";
import Statistics from "./components/Statistics/Statistics";
import FeedbackOptions from "./components/FeedbackOptions/FeedbackOptions";

const App = () => {
  // state = {
  //   good: 0,
  //   neutral: 0,
  //   bad: 0,
  //   total: 0,
  //   positivePercentage: 0,
  // };

  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);
  const [total, setTotal] = useState(0);
  const [positivePercentage, setPositivePercentage] = useState(0);

  // addFeedback = (evt) => {
  //   const objKey = evt.target.textContent.toLowerCase();
  //   // this.setState((prevState) => {
  //   //   return {
  //   //     [objKey]: prevState[objKey] + 1,
  //   //   };
  //   // });
  // };

  countTotalFeedback = () => {
    // this.setState((prevState) => ({
    //   total: prevState.total + 1,
    // }));setTotal((prevTotal) => prevTotal + 1);
  };

  countPositiveFeedbackPercentage = () => {
    this.setState((prevState) => ({
      positivePercentage: Math.round((prevState.good / prevState.total) * 100),
    }));
    setPositivePercentage((prevPositivePercentage) =>
      Math.round((good / total) * 100)
    );
  };

    // const { good, neutral, bad, total, positivePercentage } = this.state;
    return (
      <>
        <Section title={"Please leave feedback"}>
          <FeedbackOptions
            options={["Good", "Neutral", "Bad"]}
            onLeaveFeedback={(evt) => {
              this.addFeedback(evt);
              this.countTotalFeedback();
              this.countPositiveFeedbackPercentage();
            }}
          />
        </Section>

        <Section title={"Statistics"}>
          {total === 0 ? (
            <Notification />
          ) : (
            <Statistics
              good={good}
              neutral={neutral}
              bad={bad}
              total={total}
              positivePercentage={positivePercentage}
            />
          )}
        </Section>
      </>
    );

};

export default App;
